const TodoReducer = (state = { todos: [] }, action) => {
    switch (action.type) {
        case 'ADD_TODO':
            return { ...state, todos: [...state.todos, action.payload] }
        case 'REMOVE_TODO':
            return { ...state, todos: state.todos.filter(todo => todo.key !== action.payload)}
        default:
            return state
    }
}

export default TodoReducer
